mapper.select:
SELECT t1.*,t2.code as 'process_category_code',t2.name as 'process_category_name' FROM wp_process_model t1 LEFT JOIN wp_process_category t2 ON t1.category_id = t2.id where t1.deleted = 0
AND t1.diagram_name LIKE CONCAT(#{req.diagramName, jdbcType=VARCHAR},'%')
AND t2.code = #{req.processCategoryCode, jdbcType=VARCHAR}
AND t1.state = #{req.state, jdbcType=INTEGER}
AND t1.state > 0
ORDER BY t1.created_time DESC

SELECT count(1) FROM wp_process_model WHERE deleted =0
AND id != #{id, jdbcType=VARCHAR}
AND LOCATE(#{key, jdbcType=VARCHAR}, process_definition_keys) > 0

mapper.delete:
DELETE
FROM wp_process_model
WHERE id = #{id, jdbcType=VARCHAR}
